sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    api.setState("callerSysId", api.data.record.form.fields.caller.value);
    if (api.data.record.form.fields.caller.value) {
        console.log('value present');
        api.data.look_up_user_record.refresh();
    }


}
